import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(){
    super();
    this.state={
      advice:''
    }
  }
  componentDidMount(){
    const url="	https://api.adviceslip.com/advice"
     const advice =fetch(url).then(Response=>Response.json()).then(
      this.setState({
        advice:advice
      })
     );


  }
  render(){
    return (
      <div className="App">
        <h1>{this.state.advice}</h1>
      </div>
    );
  }
}

export default App;
